import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import "../styles/global.css";
import "../styles/components/telemetry.css";

const Telemetry = () => {
  // Telemetri verileri için state'ler
  const [telemetryData, setTelemetryData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // Video akışı için state'ler
  const canvasRef = useRef(null);
  const socketRef = useRef(null);
  const [isConnected, setIsConnected] = useState(false);

  // Telemetri verilerini çekme
  useEffect(() => {
    const controller = new AbortController();
    const signal = controller.signal;

    const fetchTelemetry = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:5000/telemetry", {
          signal,
        });
        setTelemetryData((prevData) =>
          JSON.stringify(prevData) !== JSON.stringify(response.data)
            ? response.data
            : prevData
        );
        setLoading(false);
        setError(null);
      } catch (error) {
        if (axios.isCancel(error)) {
          console.log("Telemetri isteği iptal edildi.");
        } else {
          console.error("Telemetri verisi alınırken hata oluştu:", error);
          setError("Telemetri verisi alınırken bir hata oluştu.");
          setLoading(false);
        }
      }
    };

    fetchTelemetry();
    const interval = setInterval(fetchTelemetry, 1000);

    return () => {
      clearInterval(interval);
      controller.abort();
    };
  }, []);

  // WebSocket bağlantısı için
  useEffect(() => {
    const connectWebSocket = () => {
      console.log("WebSocket bağlantısı başlatılıyor...");
      socketRef.current = new WebSocket("ws://localhost:8765");

      socketRef.current.onopen = () => {
        console.log("WebSocket bağlantısı başarılı!");
        setIsConnected(true);
      };

      socketRef.current.onerror = (error) => {
        console.error("WebSocket bağlantı hatası:", error);
        setTimeout(connectWebSocket, 5000);
      };

      socketRef.current.onclose = () => {
        console.log("WebSocket bağlantısı kapandı.");
        setIsConnected(false);
        setTimeout(connectWebSocket, 5000);
      };

      socketRef.current.onmessage = (event) => {
        if (!canvasRef.current) return;

        const arrayBuffer = event.data;
        const blob = new Blob([arrayBuffer], { type: "image/jpeg" });
        const imageUrl = URL.createObjectURL(blob);

        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");
        const img = new Image();

        img.onload = () => {
          if (!canvasRef.current) return;
          ctx.clearRect(0, 0, canvas.width, canvas.height);
          ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
          URL.revokeObjectURL(imageUrl);
        };
        img.src = imageUrl;
      };
    };

    connectWebSocket();

    return () => {
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);

  return (
    <div className="telemetry-wrapper">
      {/* Yeşil Konteynır (Video Akışı) */}
      <div className="telemetry-container green-container">
        <h3>CANLI GÖRÜNTÜ</h3>
        <div className="video-stream-container">
          <div className={`connection-status ${isConnected ? 'connected' : 'disconnected'}`}>
            {isConnected ? 'Bağlantı başarılı' : 'Bağlantı kesildi'}
          </div>
          <canvas ref={canvasRef} width="640" height="480" />
        </div>
      </div>
      
      {/* Kırmızı Konteynır (Telemetri Verileri) */}
      <div className="telemetry-container red-container">
        {loading ? (
          <div className="loading-message">Telemetri verisi yükleniyor...</div>
        ) : error ? (
          <div className="error-message">{error}</div>
        ) : (
          <div className="telemetry-content">
            {telemetryData && (
              <>
                <p><strong>Takım Numarası:</strong> {telemetryData.takim_numarasi}</p>
                <p><strong>Hedef Merkez X:</strong> {telemetryData.hedef_merkez_X}</p>
                <p><strong>Hedef Merkez Y:</strong> {telemetryData.hedef_merkez_Y}</p>
                <p><strong>Hedef Genişlik:</strong> {telemetryData.hedef_genislik}</p>
                <p><strong>Hedef Yükseklik:</strong> {telemetryData.hedef_yukseklik}</p>
                <p><strong>İHA Enlem:</strong> {telemetryData.iha_enlem}</p>
                <p><strong>İHA Boylam:</strong> {telemetryData.iha_boylam}</p>
                <p><strong>İHA Yükseklik:</strong> {telemetryData.iha_irtifa}</p>
                <p><strong>İHA Dikilme:</strong> {telemetryData.iha_dikilme}</p>
                <p><strong>İHA Yatış:</strong> {telemetryData.iha_yatis}°</p>
                <p><strong>İHA Yönelme:</strong> {telemetryData.iha_yonelme}</p>
                <p><strong>İHA Hız:</strong> {telemetryData.iha_hiz}</p>
                <p><strong>İHA Batarya:</strong> {telemetryData.iha_batarya}%</p>
                <p>
                  <strong>GPS Saati:</strong>{" "}
                  {telemetryData?.gps_saati?.saat ?? "00"}:
                  {telemetryData?.gps_saati?.dakika ?? "00"}:
                  {telemetryData?.gps_saati?.saniye ?? "00"}
                </p>
              </>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Telemetry;
