import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import "../styles/global.css";
import "../styles/components/telemetry.css";
import "../styles/components/iletisim.css";

const CombinedTelemetry = () => {
  // Tüm veriler için state'ler
  const [telemetryData, setTelemetryData] = useState(null);
  const [videoData, setVideoData] = useState(null);
  const [loading, setLoading] = useState({
    telemetry: true,
    video: true
  });
  const [error, setError] = useState({
    telemetry: null,
    video: null
  });

  // Video akışı için ref'ler
  const canvasRef = useRef(null);
  const socketRef = useRef(null);
  const [isConnected, setIsConnected] = useState(false);

  // Telemetri verilerini çekme
  const fetchTelemetryData = async (signal) => {
    try {
      const response = await axios.get("http://127.0.0.1:5000/telemetry", {
        signal,
      });
      setTelemetryData((prevData) =>
        JSON.stringify(prevData) !== JSON.stringify(response.data)
          ? response.data
          : prevData
      );
      setLoading(prev => ({...prev, telemetry: false}));
      setError(prev => ({...prev, telemetry: null}));
    } catch (err) {
      if (axios.isCancel(err)) {
        console.log("Telemetri isteği iptal edildi.");
      } else {
        console.error("Telemetri verisi alınırken hata:", err);
        setError(prev => ({...prev, telemetry: "Telemetri verisi alınamadı"}));
        setLoading(prev => ({...prev, telemetry: false}));
      }
    }
  };

  // Video verilerini çekme
  const fetchVideoData = async (signal) => {
    try {
      const response = await axios.get("http://127.0.0.1:5000/video", {
        responseType: 'arraybuffer',
        signal,
      });
      setVideoData(response.data);
      setLoading(prev => ({...prev, video: false}));
      setError(prev => ({...prev, video: null}));
      
      // Canvas'a çizim yap
      if (canvasRef.current) {
        const blob = new Blob([response.data], { type: "image/jpeg" });
        const imageUrl = URL.createObjectURL(blob);
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");
        const img = new Image();
        
        img.onload = () => {
          ctx.clearRect(0, 0, canvas.width, canvas.height);
          ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
          URL.revokeObjectURL(imageUrl);
        };
        img.src = imageUrl;
      }
    } catch (err) {
      if (axios.isCancel(err)) {
        console.log("Video isteği iptal edildi.");
      } else {
        console.error("Video verisi alınırken hata:", err);
        setError(prev => ({...prev, video: "Video verisi alınamadı"}));
        setLoading(prev => ({...prev, video: false}));
      }
    }
  };

  // WebSocket bağlantısı
  useEffect(() => {
    const connectWebSocket = () => {
      socketRef.current = new WebSocket("ws://localhost:8765");

      socketRef.current.onopen = () => {
        setIsConnected(true);
      };

      socketRef.current.onerror = (error) => {
        console.error("WebSocket hatası:", error);
        setTimeout(connectWebSocket, 5000);
      };

      socketRef.current.onclose = () => {
        setIsConnected(false);
        setTimeout(connectWebSocket, 5000);
      };

      socketRef.current.onmessage = (event) => {
        if (!canvasRef.current) return;
        
        const arrayBuffer = event.data;
        const blob = new Blob([arrayBuffer], { type: "image/jpeg" });
        const imageUrl = URL.createObjectURL(blob);
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");
        const img = new Image();

        img.onload = () => {
          ctx.clearRect(0, 0, canvas.width, canvas.height);
          ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
          URL.revokeObjectURL(imageUrl);
        };
        img.src = imageUrl;
      };
    };

    connectWebSocket();
    return () => {
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);

  // API'lerden veri çekme
  useEffect(() => {
    const controller = new AbortController();
    const signal = controller.signal;

    const fetchAllData = async () => {
      await fetchTelemetryData(signal);
      await fetchVideoData(signal);
    };

    fetchAllData();
    const interval = setInterval(fetchAllData, 1000);

    return () => {
      clearInterval(interval);
      controller.abort();
    };
  }, []);

  return (
    <div className="combined-container">
      {/* Video Akışı Bölümü */}
      <div className="video-section">
        <h2>Video Akışı</h2>
        <div className="connection-status">
          {isConnected ? (
            <span style={{ color: 'green' }}>CANLI: WebSocket bağlantısı aktif</span>
          ) : (
            <span style={{ color: 'orange' }}>Bağlantı kuruluyor...</span>
          )}
        </div>
        
        {error.video && !loading.video && (
          <div className="error-message">{error.video}</div>
        )}
        
        <canvas 
          ref={canvasRef} 
          width="640" 
          height="480" 
          className="video-canvas"
        />
      </div>

      {/* Telemetri Verileri Bölümü */}
      <div className="telemetry-section">
        <h2>Telemetri Verileri</h2>
        
        {error.telemetry && !loading.telemetry && (
          <div className="error-message">{error.telemetry}</div>
        )}
        
        {/* Özet Bilgiler */}
        <div className="telemetry-container green-container">
          <h3>DURUM BİLGİLERİ</h3>
          {loading.telemetry ? (
            <p>Yükleniyor...</p>
          ) : telemetryData ? (
            <>
              <p><strong>Takım Numarası:</strong> {telemetryData.takim_numarasi}</p>
              <p><strong>GPS Saati:</strong> 
                {telemetryData?.gps_saati?.saat ?? "00"}:
                {telemetryData?.gps_saati?.dakika ?? "00"}:
                {telemetryData?.gps_saati?.saniye ?? "00"}
              </p>
              <p><strong>İHA Batarya:</strong> {telemetryData.iha_batarya}%</p>
              <p><strong>Son Güncelleme:</strong> {new Date().toLocaleTimeString()}</p>
            </>
          ) : (
            <p>Veri yok</p>
          )}
        </div>

        {/* Detaylı Telemetri */}
        <div className="telemetry-container red-container">
          <h3>DETAYLI TELEMETRİ</h3>
          {loading.telemetry ? (
            <p>Yükleniyor...</p>
          ) : telemetryData ? (
            <div className="telemetry-content">
              <p><strong>Hedef Merkez X:</strong> {telemetryData.hedef_merkez_X}</p>
              <p><strong>Hedef Merkez Y:</strong> {telemetryData.hedef_merkez_Y}</p>
              <p><strong>Hedef Genişlik:</strong> {telemetryData.hedef_genislik}</p>
              <p><strong>Hedef Yükseklik:</strong> {telemetryData.hedef_yukseklik}</p>
              <p><strong>İHA Enlem:</strong> {telemetryData.iha_enlem}</p>
              <p><strong>İHA Boylam:</strong> {telemetryData.iha_boylam}</p>
              <p><strong>İHA Yükseklik:</strong> {telemetryData.iha_irtifa}</p>
              <p><strong>İHA Dikilme:</strong> {telemetryData.iha_dikilme}</p>
              <p><strong>İHA Yatış:</strong> {telemetryData.iha_yatis}°</p>
              <p><strong>İHA Yönelme:</strong> {telemetryData.iha_yonelme}</p>
              <p><strong>İHA Hız:</strong> {telemetryData.iha_hiz}</p>
            </div>
          ) : (
            <p>Veri yok</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default CombinedTelemetry;
